$(document).ready(function() {     $(".contact").hide();});// Show/Hide Contact$("#contactToggle").click(function() {    $(".contact form").toggle();});// Page Fade In$('.header, .bodySection, .dropDown, .footer, .contact').addClass('headline, .cssmenu');$('.header, .cssmenu, .footer').hide().fadeIn(500);$('.bodySection').hide().fadeIn(1500);$('.contact').hide().delay(0).fadeIn(500);